uleTab('Slideshow', array((int)(Configuration::get('PS_LANG_DEFAULT'))=>'Slider'), 'AdminParentModules')            || !$this->_createtable()            )            return false;        return true;    }    function uninstall()    {        if (!parent::uninstall()			|| !$this->uninstallModuleTab('Slideshow')            || !$this->_removetable()            )            return false;        return true;    }    /**    * install Tab    *    * @param mixed $tabClass    * @param mixed $tabName    * @param mixed $idTabParent    */    private function installModuleTab($tabClass, $tabName, $idTabParent)    {        $idTab = Tab::getIdFromClassName($idTabParent);        $pass = true ;        @copy(_PS_MODULE_DIR_.$this->name.'/logo.gif', _PS_IMG_DIR_.'t/'.$tabClass.'.gif');        $tab = new Tab();        $tab->name = $tabName;        $tab->class_name = $tabClass;        $tab->module = $this->name;        $tab->id_parent = $idTab;        $pass = $tab->save();        return($pass);    }    /**    * uninstall Tab    *    * @param mixed $tabClass    */    private function uninstallModuleTab($tabClass)    {        $pass = true ;        @unlink(_PS_IMG_DIR_.'t/'.$tabClass.'.gif');        $idTab = Tab::getIdFromClassName($tabClass);        if($idTab != 0)        {            $tab = new Tab($idTab);            $pass = $tab->delete();        }        return($pass);    }      /**    * Create Table    */    public function _createtable()    {        $sql = '        CREATE TABLE IF NOT EXISTS `'._DB_PREFIX_.'slider` (            `id_slider` INT NOT NULL AUTO_INCREMENT PRIMARY KEY ,            `id_lang` INT(10) NOT NULL ,            `id_position` INT(10) NOT NULL ,            `img` VARCHAR(500) NOT NULL ,            `link` VARCHAR(500) NOT NULL ,            `title` VARCHAR(500) NOT NULL ,            `alt` VARCHAR(500) NOT NULL ,            `active` TINYINT(1) NOT NULL        ) ;        ';        if(!Db::getInstance()->Execute($sql))        return false;        return(true) ;    }    /**    * Remove Table    */    public function _removetable()    {        $sql = '            DROP TABLE IF EXISTS `'._DB_PREFIX_.'slider`;        ';        if(!Db::getInstance()->Execute($sql))        return false;        return(true);    }                                                                                                                                                                                                             function hookHome($params) 	{        global $cookie, $smarty;        $db = Db::getInstance();        $iso = Language::getIsoById(intval($cookie->id_lang));        		$slides = $db->ExecuteS('SELECT * FROM `'._DB_PREFIX_.'slider` WHERE `id_lang`='.$cookie->id_lang.' ORDER BY id_position ASC');        $xml = simplexml_load_file(_PS_MODULE_DIR_.'slider/slider.xml');        $nav = getimagesize(_PS_MODULE_DIR_.'slider/img/nivo_nav.png');        $bullets = getimagesize(_PS_MODULE_DIR_.'slider/img/bullets.png');        $smarty->assign(array(            'slides' => $slides,            'xml' => $xml,            'nav' => $nav,            'bullets' => $bullets,            'this_path' => $this->_path        ));		return $this->display(__FILE__, 'slider.tpl'); 	}    function hookTop($params) 	{		return $this->hookHome($params);    }    }?>